import React, { useState } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import'./log.css';
import { useNavigate } from 'react-router-dom';
const [result, setResult] = useState(null);


const Login = () => {
    const [error, setError] = useState(null);
    const [result, setResult] = useState(null);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        const url = 'https://json-storage-api.p.rapidapi.com/datalake';
        const options = {
            method: 'POST',
            headers: {
                'content-type': 'application/json',
                'X-RapidAPI-Key': '66d105c179msh9c5b82c1cf62460p15aa72jsn63c558e1dc1e',
                'X-RapidAPI-Host': 'json-storage-api.p.rapidapi.com'
            },
            body: JSON.stringify({
                '@context': [
                    'http://schema4i.org/Thing.jsonld',
                    'http://schema4i.org/Action.jsonld',
                    'http://schema4i.org/SearchAction.jsonld'
                ],
                '@type': 'SearchAction',
                Object: {
                    '@context': [
                        'http://schema4i.org/Thing.jsonld',
                        'http://schema4i.org/Filter',
                        'http://schema4i.org/DataLakeItem',
                        'http://schema4i.org/UserAccount'
                    ],
                    '@type': 'Filter',
                    FilterItem: {
                        '@type': 'DataLakeItem',
                        Creator: {
                            '@type': 'UserAccount',
                            Identifier: username
                        },
                        About: {
                            '@type': 'Organization',
                            Username: username,
                            Password: password
                        }
                    }
                }
            })
        };

        try {
            const response = await fetch(url, options);
            const result = await response.json();
            if (result.Result && result.Result.NumberOfItems > 0) {
               
                navigate('/transaction');
            } else {
                setError('Invalid username or password');
            }
        } catch (error) {
            setError('An error occurred. Please try again.');
            console.error(error);
        }
    };

    return (
        <Row>
            <Container fluid>
                <Col md={0}></Col>
                <Col md={20}>
                    <form onSubmit={handleSubmit}>
                        <div className="bg-image">
                            <div className="row row-cols-1 row-cols-md-2 g-4">
                                <div className="col shadow p-3 mb-5 bg-body-tertiary rounded fromtable">
                                    <div className="mb-3 login">
                                        <h4 style={{ padding: "10px" }}>Sign in</h4>
                                        <label htmlFor="username" className="form-label" style={{ padding: "10px" }}>User Name</label>
                                        <input
                                            type="text"
                                            placeholder="Enter username"
                                            value={username}
                                            onChange={(e) => setUsername(e.target.value)}
                                        />
                                        <label htmlFor="password" className="form-label" style={{ padding: "10px" }}>Password</label>
                                        <input
                                            type="password"
                                            placeholder="Password"
                                            value={password}
                                            onChange={(e) => setPassword(e.target.value)}
                                        />
                                       <br></br>
                                        <Link to="/Transaction">
                                         <button className="btn btn-secondary">Sign up</button>
                                        </Link>
                                    </div>
                                    {error && <p>Error: {error.message}</p>}
                                    {result && <pre>{result}</pre>}
                                    <br />
                                    
                                </div>
                            </div>
                        </div>
                    </form>
                </Col>
            </Container>
        </Row>
    );
};

export default Login;
