import React, { useState } from 'react';
import './log.css';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col } from 'react-bootstrap';

const Etransfer = ({ type, onSubmit }) => {
    const [accountNumber, setAccountNumber] = useState('');
    const [amount, setAmount] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        
        if (!accountNumber.trim() || !amount.trim()) {
            alert('Please enter both account number and amount.');
            return;
        }
        
        const transactionData = {
            accountNumber: accountNumber.trim(),
            amount: parseFloat(amount.trim()),
            type: type
        };
        
        if (typeof onSubmit === 'function') {
            onSubmit(transactionData);
        } else {
            console.error('onSubmit is not a function');
        }
        
        setAccountNumber('');
        setAmount('');
    };

    return (
        <Row>
            <Container fluid>
                <Col md={0}></Col>
                <Col md={20}>
                    <form onSubmit={handleSubmit}>
                        <h2>{type === 'deposit' ? 'Deposit' : 'Withdraw'} Form</h2>
                        <div className="row row-cols-1 row-cols-md-2 g-4">
                            <div className="col shadow p-3 mb-5 bg-body-tertiary rounded fromtable">
                                <div className="mb-3 login">
                                    <h4 style={{ padding: "10px" }}>E Transfer</h4>
                                    <label htmlFor="payee" className="form-label" style={{ padding: "10px" }}>Payee: </label>
                                    <input type="text" className="form-control" id="exampleInputusername" aria-describedby="username" />
                                    <label htmlFor="amount">Amount</label>
                                    <input 
                                        type="number" 
                                        className="form-control" 
                                        id="amount" 
                                        value={amount} 
                                        onChange={(e) => setAmount(e.target.value)} 
                                        required 
                                    />
                                    <input type="payee" className="form-control" id="exampleInputpayee" />
                                    <br /><br />
                                    <Link to="/Transaction">
                                        <button type="submit" className="btn btn-secondary">Submit</button>
                                    </Link>
                                    <br /><br /><br />
                                </div>
                            </div>
                        </div>
                    </form>
                </Col>
         </Container>
        </Row>
    );
};

export default Etransfer;
