import React, {useState} from 'react';
import './log.css';
import Background from './skyline.jpg';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col } from 'react-bootstrap';
import axios from 'axios';

const Login = () => {
  const [username, setusername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    const response = await axios.post('/api/login', { username, password });
       if (response.status === 200) {
        window.location.href = '/Transaction';

}
  };

  return (
      <row>
      <Container fluid>
      <Col md={0} ></Col>
      <Col md={20}>
        <form onSubmit={handleSubmit}>
        <div class="bg-image"> 
        <div class="row row-cols-1 row-cols-md-2 g-4">
        <div class="col shadow p-3 mb-5 bg-body-tertiary rounded fromtable" >
        <div class="mb-3" className='login'>
          <h4 style={{padding: "10px"}}>Wellcome. Please log in before using other functions.</h4>
          <label for="usernam" class="form-label" style={{padding: "10px"}} >User Name</label>
          <input
            type="username"
            value={username}
            class="form-control" 
            id="exampleInputusername" 
            aria-describedby="username"
            onChange={(e) => setusername(e.target.value)}
            required
          />
          <label for="password" class="form-label" style={{padding: "10px"}} >Password</label>
          <input
            type="password"
            class="form-control" 
            id="exampleInputPassword1"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <br></br>
          <br></br>
          {error && <p className="error">{error}</p>}
        <button type="submit" class="btn btn-secondary" >Login</button>
        <br></br>
        <br></br>
        <br></br>
        </div>
        </div>
        </div>
        </div>
      </form>
      </Col>
            </Container>
            </row>

);
};

export default Login;
