import React from 'react';
import './log.css';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col } from 'react-bootstrap';

const etransfer = () => {
    const [accountNumber, setAccountNumber] = useState(''); 
    const [amount, setAmount] = useState(''); 

    const handleSubmit = (e) => { 
        e.preventDefault(); 
       
        if (!accountNumber.trim() || !amount.trim()) { 
        alert('Please enter both account number and amount.'); 
        return; 
         } 
        
        const transactionData = { 
            accountNumber: accountNumber.trim(), 
            amount: parseFloat(amount.trim()),
            type: type 
        }; 
       
        onSubmit(transactionData); 
        
        
        setAccountNumber(''); 
        setAmount(''); 
    }; 

    return (
      <row>
      <Container fluid>
      <Col md={0} ></Col>
      <Col md={20}>
        <form onSubmit={handleSubmit}> 
        <h2>{type === 'deposit' ? 'Deposit' : 'Withdraw'} Form</h2>
        <div class="row row-cols-1 row-cols-md-2 g-4">
        <div class="col shadow p-3 mb-5 bg-body-tertiary rounded fromtable" >
        <div class="mb-3" className='login'>
          <h4 style={{padding: "10px"}}>E Transfer</h4>
          <label for="usernam" class="form-label" style={{padding: "10px"}} >Payment: </label>
          <input type="username" class="form-control" id="exampleInputusername" aria-describedby="username" />
          <label for="password" class="form-label" style={{padding: "10px"}} >Amount: </label>
          <input 
              type="number" 
              className="form-control" 
              id="amount" 
              value={amount} 
              onChange={(e) => setAmount(e.target.value)} 
              required 
                        /> 
          <input type="password" class="form-control" id="exampleInputPassword1"  />
          <br></br>
          <br></br>
          <Link to="/Transaction">
        <button type="submit" class="btn btn-secondary" >Login</button>
        </Link>
        <br></br>
        <br></br>
        <br></br>
        </div>
        </div>
        </div>
      </form>
      </Col>
            </Container>
            </row>

    );
};

export default etransfer;
